namespace Sg\DatatablesBundle\Datatable\Column;

use Sg\DatatablesBundle\Datatable\Action\MultiselectAction;

use Symfony\Component\OptionsResolver\OptionsResolver;
use Symfony\Component\PropertyAccess\Exception\InvalidArgumentException;
use Closure;

/**
 * Class MultiselectColumn
 *
 * @package Sg\DatatablesBundle\Datatable\Column
 */
class MultiselectColumn extends ActionColumn
{
    /**
     * HTML attributes for the checkboxes.
     *
     * @var array
     */
    protected $attributes;

    /**
     * A checkbox value, generated by column name.
     *
     * @var string
     */
    protected $value;

    /**
     * Render checkbox only if conditions are True.
     *
     * @var Closure
     */
    protected $renderCheckboxIf;

    //-------------------------------------------------
    // ColumnInterface
    //-------------------------------------------------

    /**
     * {@inheritdoc}
     */
    public function getTemplate()
    {
        return 'SgDatatablesBundle:Column:multiselect.html.twig';
    }

    /**
     * {@inheritdoc}
     */
    public function addDataToOutputArray(&$row)
    {
        $row['sg_datatables_cbox'] = $this->isRenderCheckboxIf($row);
    }

    /**
     * {@inheritdoc}
     */
    public function getAlias()
    {
        return 'multiselect';
    }

    //-------------------------------------------------
    // OptionsInterface
    //-------------------------------------------------

    /**
     * {@inheritdoc}
     */
    public function configureOptions(OptionsResolver $resolver)
    {
        parent::configureOptions($resolver);

        $resolver->setDefaults(array(
            'attributes' => array(),
            'value' => 'id',
            'render_checkbox_if' => null
        ));

        $resolver->setAllowedTypes('attributes', 'array');
        $resolver->setAllowedTypes('value', 'string');
        $resolver->setAllowedTypes('render_checkbox_if', array('Closure', 'null'));

        return $this;
    }

    //-------------------------------------------------
    // Helper
    //-------------------------------------------------

    /**
     * Is renderCheckboxIf.
     *
     * @param array $row
     *
     * @return boolean
     */
    public function isRenderCheckboxIf(array $row = array())
    {
        if ($this->renderCheckboxIf instanceof Closure) {
            return call_user_func($this->renderCheckboxIf, $row);
        }

        return true;
    }

    //-------------------------------------------------
    // Getters && Setters
    //-------------------------------------------------

    /**
     * Set actions.
     *
     * @param array $actions
     *
     * @return $this
     */
    public function setActions(array $actions)
    {
        foreach ($actions as $action) {
            $newAction = new MultiselectAction();
            $newAction->setTableName($this->tableName);
            $this->actions[] = $newAction->setupOptionsResolver($action);
        }

        return $this;
    }

    /**
     * Set checkbox attributes.
     *
     * @param array $attributes
     *
     * @throws InvalidArgumentException
     * @return $this
     */
    public function setAttributes(array $attributes)
    {
        $this->attributes = $attributes;

        if (array_key_exists('type', $this->attributes)) {
            throw new InvalidArgumentException('The type attribute is not supported.');
        }
        if (array_key_exists('value', $this->attributes)) {
            throw new InvalidArgumentException('The value attribute is not supported.');
        }
        if (array_key_exists('name', $this->attributes)) {
            $this->attributes['name'] = $this->tableName . '_multiselect_checkbox ' . $this->attributes['name'];
        } else {
            $this->attributes['name'] = $this->tableName . '_multiselect_checkbox';
        }
        if (array_key_exists('class', $this->attributes)) {
            $this->attributes['class'] = $this->tableName . '_multiselect_checkbox ' . $this->attributes['class'];
        } else {
            $this->attributes['class'] = $this->tableName . '_multiselect_checkbox';
        }

        return $this;
    }

    /**
     * Get checkbox attributes.
     *
     * @return array
     */
    public function getAttributes()
    {
        return $this->attributes;
    }

    /**
     * Set value.
     *
     * @param string $value
     *
     * @return $this
     */
    public function setValue($value)
    {
        $this->value = $value;

        return $this;
    }

    /**
     * Get value.
     *
     * @return string
     */
    public function getValue()
    {
        return $this->value;
    }

    /**
     * Set renderCheckboxIf.
     *
     * @param Closure $renderCheckboxIf
     *
     * @return $this
     */
    public function setRenderCheckboxIf($renderCheckboxIf)
    {
        $this->renderCheckboxIf = $renderCheckboxIf;

        return $this;
    }

    /**
     * Get renderCheckboxIf.
     *
     * @return Closure
     */
    public function getRenderCheckboxIf()
    {
        return $this->renderCheckboxIf;
    }
}
